<body>
    <br>
    <div class="container">
        <h1 class="text-center">Listado de Animales</h1>
    </div>
    <br>
    <div class="container">
    <div class ="container">
        <a href=""Class="btn btn-success">Agregar</a>
        </div>
        <table class="table table-hover">
            <thead>
                <tr>
                    <th scope="col">Nombre</th>
                    <th scope="col">Tipo</th>
                    <th scope="col">Raza</th>
                    <th scope="col">Sexo</th>
                    <th scope="col">Edad</th>
                    <th scope="col">Tamaño</th>
                    <th scope="col">Descripcion</th>
                    <th scope="col">Albergue</th>
                    <th scope="col">Accion</th>
                </tr>
            </thead>
            <tbody>
                <?php
                require('include/dbconn.php');
                $database = new Connection();
                $db = $database->open();
                try {
                    $sql = $db->prepare("SELECT animal.Nombre AS Nombre_Animal, animal.Tipo, animal.Raza, animal.Sexo, animal.Edad, animal.Tamaño, animal.Descripcion, albergue.Nombre AS Nombre_Albergue
                    FROM animal
                    INNER JOIN albergue ON animal.Id_Albergue = albergue.Id_Albergue");

                
                    $sql->execute();
                    $resultados = $sql->fetchAll(PDO::FETCH_ASSOC);

                    
                    foreach ($resultados as $resultado) {
                        echo '<tr>';
                        echo '<th scope="row">' . $resultado["Nombre_Animal"] . '</th>';
                        echo '<td>' . $resultado["Tipo"] . '</td>';
                        echo '<td>' . $resultado["Raza"] . '</td>';
                        echo '<td>' . $resultado["Sexo"] . '</td>';
                        echo '<td>' . $resultado["Edad"] . '</td>';
                        echo '<td>' . $resultado["Tamaño"] . '</td>';
                        echo '<td>' . $resultado["Descripcion"] . '</td>';
                        echo '<td>' . $resultado["Nombre_Albergue"] . '</td>';
                        echo '<td>';
                        echo '<a href=""><img src="./public/img/eliminar.png" width="20px" alt=""></a>';
                        echo '<a href=""><img src="./public/img/editar.png" width="20px" alt=""></a>';
                        echo '</td>';
                        echo '</tr>';
                    }
                } catch (PDOException $e) {
                    echo "Error: " . $e->getMessage();
                }
                $database->close();
                ?>

            </tbody>
        </table>
        
    </div>
